"use strict";


/* Module API:
 * var primecheck = require('primecheck')
 * primecheck(number) // returns true if a prime number
 *
 */


function primecheck() {
    var arg0 = arguments[0]

  	if (!arg0) return false;
  	if (isNaN(arg0)) return false;

  	var isDiv = false;

  	for (var i=2; i<=9; i++) {
  		if (arg0 % i == 0 && i != arg0) {
  			isDiv = true;
  			break;
      }
  	}
  	return !isDiv;
}



module.exports = primecheck
